INSERT INTO `NotificationTemplates` (`TemplateName`, `Channel`, `TokenType`, `Subject`, `Body`, `IsActive`, `CreatedAt`, `UpdatedAt`) VALUES
('Verify Email Template', 'email', 'NewUser', 'Verify Your Email', '<!DOCTYPE html>\n<html lang=\"en\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Verify Your Email</title>\n    <style>\n        body {\n            font-family: Arial, sans-serif;\n            margin: 0;\n            padding: 0;\n            background-color: #f9f9f9;\n        }\n\n        .email-container {\n            max-width: 600px;\n            margin: 0 auto;\n            background-color: #ffffff;\n            padding: 20px;\n            border: 1px solid #dddddd;\n            border-radius: 8px;\n        }\n\n        .button {\n            display: inline-block;\n            background-color: #007bff;\n            color: #ffffff;\n            text-decoration: none;\n            padding: 10px 20px;\n            border-radius: 5px;\n            font-weight: bold;\n        }\n\n            .button:hover {\n                background-color: #0056b3;\n            }\n    </style>\n</head>\n<body>\n<div class=\"email-container\">\n    <h2>Hey {{First_Name}}, welcome to Aw3s0.me!</h2>\n    <p>Thank you for signing up for our website. Please verify your email address to activate your account.</p>\n    <p>\n        <a href=\"{{URL}}/verify?token={{Verification_Code}}\" class=\"button\">Verify Email</a>\n    </p>\n    <p>If you didn’t sign up for an account, you can safely ignore this email.</p>\n    <p>Thank you,<br>The Aw3s0.me Team</p>\n</div>\n</body>\n</html>', 1, '2024-12-29 12:05:09', '2025-01-12 19:59:53'),
('Reset Password', 'email', 'ForgotPassword', 'Change your password', '<!DOCTYPE html>\n<html lang=\"en\">\n<head>\n    <meta charset=\"UTF-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <title>Change your password</title>\n    <style>\n        body {\n            font-family: Arial, sans-serif;\n            margin: 0;\n            padding: 0;\n            background-color: #f9f9f9;\n        }\n\n        .email-container {\n            max-width: 600px;\n            margin: 0 auto;\n            background-color: #ffffff;\n            padding: 20px;\n            border: 1px solid #dddddd;\n            border-radius: 8px;\n        }\n\n        .button {\n            display: inline-block;\n            background-color: #007bff;\n            color: #ffffff;\n            text-decoration: none;\n            padding: 10px 20px;\n            border-radius: 5px;\n            font-weight: bold;\n        }\n\n            .button:hover {\n                background-color: #0056b3;\n            }\n    </style>\n</head>\n<body>\n<div class=\"email-container\">\n    <h2>Password recovery for Aw3s0.me!</h2>\n    <p>Click the button to change your password</p>\n    <p>\n        <a href=\"{{URL}}/reset?token={{Verification_Code}}\" class=\"button\">Verify Email</a>\n    </p>\n    <p>If you didn’t initiate a password reset, you can safely ignore this email.</p>\n    <p>Thank you,<br>The Aw3s0.me Team</p>\n</div>\n</body>\n</html>', 1, '2024-12-29 12:49:34', '2025-01-12 20:01:58');
