INSERT INTO `NotificationTemplates` (`TemplateName`, `Channel`, `Subject`, `Body`, `IsActive`, `CreatedAt`, `UpdatedAt`) VALUES
('Verify Email Template', 'email', 'Verify Your Email', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <title>Verify Your Email</title>\r\n    <style>\r\n        body {\r\n            font-family: Arial, sans-serif;\r\n            margin: 0;\r\n            padding: 0;\r\n            background-color: #f9f9f9;\r\n        }\r\n\r\n        .email-container {\r\n            max-width: 600px;\r\n            margin: 0 auto;\r\n            background-color: #ffffff;\r\n            padding: 20px;\r\n            border: 1px solid #dddddd;\r\n            border-radius: 8px;\r\n        }\r\n\r\n        .button {\r\n            display: inline-block;\r\n            background-color: #007bff;\r\n            color: #ffffff;\r\n            text-decoration: none;\r\n            padding: 10px 20px;\r\n            border-radius: 5px;\r\n            font-weight: bold;\r\n        }\r\n\r\n            .button:hover {\r\n                background-color: #0056b3;\r\n            }\r\n    </style>\r\n</head>\r\n<body>\r\n<div class=\"email-container\">\r\n    <h2>Hey FIRST_NAME, welcome to Aw3s0.me!</h2>\r\n    <p>Thank you for signing up for our website. Please verify your email address to activate your account.</p>\r\n    <p>\r\n        <a href=\"REPLACE_URL/verify?token=UNIQUE_VERIFICATION_TOKEN\" class=\"button\">Verify Email</a>\r\n    </p>\r\n    <p>If you didn’t sign up for an account, you can safely ignore this email.</p>\r\n    <p>Thank you,<br>The Aw3s0.me Team</p>\r\n</div>\r\n</body>\r\n</html>', 1, '2024-12-29 12:05:09', '2024-12-29 16:24:22'),
('VerifyEmail', 'email', 'Change your password', '<!DOCTYPE html>\r\n<html lang=\"en\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <title>Change your password</title>\r\n    <style>\r\n        body {\r\n            font-family: Arial, sans-serif;\r\n            margin: 0;\r\n            padding: 0;\r\n            background-color: #f9f9f9;\r\n        }\r\n\r\n        .email-container {\r\n            max-width: 600px;\r\n            margin: 0 auto;\r\n            background-color: #ffffff;\r\n            padding: 20px;\r\n            border: 1px solid #dddddd;\r\n            border-radius: 8px;\r\n        }\r\n\r\n        .button {\r\n            display: inline-block;\r\n            background-color: #007bff;\r\n            color: #ffffff;\r\n            text-decoration: none;\r\n            padding: 10px 20px;\r\n            border-radius: 5px;\r\n            font-weight: bold;\r\n        }\r\n\r\n            .button:hover {\r\n                background-color: #0056b3;\r\n            }\r\n    </style>\r\n</head>\r\n<body>\r\n<div class=\"email-container\">\r\n    <h2>Password recovery for Aw3s0.me!</h2>\r\n    <p>Click the button to change your password</p>\r\n    <p>\r\n        <a href=\"REPLACE_URL/reset?token=UNIQUE_VERIFICATION_TOKEN\" class=\"button\">Verify Email</a>\r\n    </p>\r\n    <p>If you didn’t initiate a password reset, you can safely ignore this email.</p>\r\n    <p>Thank you,<br>The Aw3s0.me Team</p>\r\n</div>\r\n</body>\r\n</html>', 1, '2024-12-29 12:49:34', '2024-12-29 16:24:25');
